function FileDetails({ file, onClose, onDelete, onDownload }) {
    const [isRenaming, setIsRenaming] = useState(false);
    const [newName, setNewName] = useState(file.name);
  
    const handleRename = () => {
      fetch(`http://localhost:8080/api/files/${file.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: newName }),
      })
        .then((response) => {
          if (response.ok) {
            file.name = newName; // Update file name locally
            setIsRenaming(false);
          } else {
            alert('Failed to rename file.');
          }
        })
        .catch((error) => console.error('Error renaming file:', error));
    };
  
    return (
      <div className="absolute top-0 left-0 w-full h-full bg-gray-900 bg-opacity-50 flex items-center justify-center">
        <div className="bg-white p-5 rounded-lg">
          <h2 className="text-xl font-bold">{file.name}</h2>
          <p>Path: {file.path}</p>
          <div className="flex space-x-4 mt-4">
            {isRenaming ? (
              <>
                <input
                  type="text"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  className="input input-bordered"
                />
                <button
                  onClick={handleRename}
                  className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
                >
                  Save
                </button>
                <button
                  onClick={() => setIsRenaming(false)}
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
                >
                  Cancel
                </button>
              </>
            ) : (
              <button
                onClick={() => setIsRenaming(true)}
                className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded"
              >
                Rename
              </button>
            )}
            <button
              onClick={() => onDelete(file.id)}
              className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
            >
              Delete
            </button>
            <button
              onClick={() => onDownload(file)}
              className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
            >
              Download
            </button>
            <button
              onClick={onClose}
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    );
  }
