import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import FileDetails from './FileDetails';  


function FileList() {
  const { directoryId } = useParams();
  const [files, setFiles] = useState([]);
  const [fileToUpload, setFileToUpload] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    const url = `http://localhost:8080/api/directories/${directoryId}/files`;
    fetch(url)
      .then(response => response.json())
      .then(data => setFiles(data))
      .catch(error => console.error('Error fetching files:', error));
  }, [directoryId]);

  const handleFileChange = (event) => {
    setFileToUpload(event.target.files[0]); // Assuming single file upload
  };

  const handleFileClick = (file) => {
    setSelectedFile(file); // Set the clicked file to be shown in details
  };

  const handleClose = () => {
    setSelectedFile(null); // Close the modal by resetting the selected file
  };

  const uploadFile = () => {
    const formData = new FormData();
    formData.append("file", fileToUpload);
    formData.append("directoryId", directoryId); // Make sure your backend can handle this
  
    fetch('http://localhost:8080/api/files/upload', {  // Adjusted to correct endpoint
      method: 'POST',
      body: formData
    })
    .then(response => {
      if (response.ok) {
        return response.json(); // Return the parsed JSON only if the response was ok
      } else {
        throw new Error('Failed to upload file');
      }
    })
    .then(data => {
      setFiles([...files, data]); // Handle your successful data here
    })
    .catch(error => console.error('Error uploading file:', error));
  };
  
  const onDownload = (file) => {
    const downloadUrl = `http://localhost:8080/api/files/download/${file.id}`;
    window.open(downloadUrl, '_blank'); // This opens a new tab and initiates the download
  };
  

  const handleDelete = (fileId) => {
    fetch(`http://localhost:8080/api/files/${fileId}`, {
      method: 'DELETE'
    })
    .then(response => {
      if (response.ok) {
        setFiles(files.filter(file => file.id !== fileId));
        setSelectedFile(null); // Close the details view if open
      }
    })
    .catch(error => console.error('Error deleting file:', error));
  };

  return (
    <div className="container mx-auto mt-5">
    <h2 className="text-xl font-semibold">Files in Directory</h2>
    <input type="file" onChange={handleFileChange} className="mb-4" />
    <button onClick={uploadFile} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">Upload File</button>
    <ul className="list-disc pl-5">
      {files.map(file => (
        <li key={file.id} className="text-blue-500 hover:text-blue-700 cursor-pointer" onClick={() => handleFileClick(file)}>
          {file.name}
        </li>
      ))}
    </ul>
    {selectedFile && <FileDetails file={selectedFile} onClose={handleClose} onDelete={handleDelete} />}
  </div>
);
}
export default FileList;
