import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import FileDetails from './FileDetails'; // Ensure you import FileDetails

function FileList() {
  const { directoryId } = useParams();
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    const url = `http://localhost:8080/api/directories/${directoryId}/files`;
    fetch(url)
      .then(response => response.json())
      .then(data => setFiles(data))
      .catch(error => console.error('Error fetching files:', error));
  }, [directoryId]);

  return (
    <div className="container mx-auto mt-5">
      <h2 className="text-xl font-semibold">Files in Directory</h2>
      <ul className="list-disc pl-5">
        {files.map(file => (
          <li key={file.id} className="text-blue-500 hover:text-blue-700 cursor-pointer"
              onClick={() => setSelectedFile(file)}>
            {file.name}
          </li>
        ))}
      </ul>
      {selectedFile && <FileDetails file={selectedFile} onClose={() => setSelectedFile(null)} />}
    </div>
  );
}

export default FileList;
