import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

function DirectoryList() {
  const [directories, setDirectories] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:8080/api/directories/')
      .then(response => response.json())
      .then(data => setDirectories(data))
      .catch(error => console.error('Error fetching directories:', error));
  }, []);

  const handleNewDirectory = (directory) => {
    fetch('http://localhost:8080/api/directories/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(directory),
    })
      .then(response => response.json())
      .then(data => {
        setDirectories([...directories, data]);
        setShowForm(false);
      })
      .catch(error => console.error('Failed to create directory:', error));
  };

  const handleDeleteDirectory = (directoryId) => {
    fetch(`http://localhost:8080/api/directories/${directoryId}`, {
      method: 'DELETE',
    })
      .then(response => {
        if (response.ok) {
          setDirectories(directories.filter(directory => directory.id !== directoryId));
          alert('Directory deleted successfully.');
        } else {
          alert('Failed to delete directory. It might not be empty.');
        }
      })
      .catch(error => console.error('Error deleting directory:', error));
  };

  const handleDirectoryClick = (id) => {
    navigate(`/directory/${id}`);
  };

  const renderDirectories = (dirs) => {
    return (
      <ul className="list-disc pl-5">
        {dirs.map((directory) => (
          <li key={directory.id} className="flex flex-col space-y-2">
            <div className="flex items-center space-x-4">
              <span
                className="text-blue-500 hover:text-blue-700 cursor-pointer"
                onClick={() => handleDirectoryClick(directory.id)}
              >
                {directory.name}
              </span>
              <button
                onClick={() => handleDeleteDirectory(directory.id)}
                className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-1 rounded"
              >
                Delete
              </button>
            </div>
            {directory.childDirectories && renderDirectories(directory.childDirectories)}
          </li>
        ))}
      </ul>
    );
  };

  return (
    <div className="container mx-auto mt-5">
      <h1 className="text-2xl font-bold text-center mb-4">Directories</h1>
      <button
        onClick={() => setShowForm(!showForm)}
        className="mb-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
      >
        {showForm ? 'Cancel' : 'Create New Directory'}
      </button>
      {showForm && (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            const name = e.target.name.value;
            const path = e.target.path.value;
            handleNewDirectory({ name, path });
          }}
        >
          <input
            type="text"
            name="name"
            placeholder="Directory Name"
            className="input input-bordered w-full max-w-xs"
          />
          <input
            type="text"
            name="path"
            placeholder="Directory Path"
            className="input input-bordered w-full max-w-xs"
          />
          <button type="submit" className="btn btn-primary">
            Create
          </button>
        </form>
      )}
      {renderDirectories(directories)}
    </div>
  );
}

export default DirectoryList;
